import React from 'react';
import {useHistory} from 'react-router-dom';


const BookAdd = (props) => {

    const history = useHistory();
    const[formData, updateFormData] = React.useState({
        name: "",
        availableCopies: 0,
        author: 1,
        category: "NOVEL"
    });

    const handleFieldChange = () => {
        updateFormData()
    };

    const submit = () => {

    };

    return (
        <div className="row mt-5">
            <div className="col-md-5">
                <form onSubmit={submit}>
                    <div className="form-group">
                        <label htmlFor="name">Book name</label>
                        <input type="text"
                               className="form-control"
                               id="name"
                               name="name"
                               required
                               placeholder="Enter book name..."
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="price">Available copies</label>
                        <input type="text"
                               className="form-control"
                               id="availableCopies"
                               name="availableCopies"
                               placeholder="Enter available copies..."
                               required
                        />
                    </div>
                    <div className="form-group">
                        <label>Author</label>
                        <select name="author" className="form-control">
                            {props.authors.map((term) => <option value={term.id}>{term.name} {term.surname}</option>)}
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Category</label>
                        <select name="category" className="form-control">
                            {props.categories.map((term) => <option value={term}>{term}</option>)}
                        </select>
                    </div>
                    <button id="submit" type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div>
    );
};


export default BookAdd;
